import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {
  nomeProduto: string = 'Curso Angular';
  //template string üëá strings definidas com crase permitem a interpola√ß√£o, permitindo colocar vari√°veis dentro da string
  //anuncio: string = `O ${this.nomeProduto} est√° em promo√ß√£o!`;
  idProduto: number = 1;
  precoProduto: number = 22.5;
  promocao: boolean = false;
  foto: string = 'assets/img/crud.png';
  dataValidade = '2021-12-31';

  listaProdutos: any[] = [
    {
      nome: 'Curso de Angular',
      precoProduto: 50.50,
      validade: '2022-12-31',
      id: 1,
    },
    {
      nome: 'Curso de React',
      precoProduto: 55.60,
      validade: '2022-12-31',
      id: 2,
      promocao: true,
    },
    {
      nome: 'Curso de Vue.js',
      precoProduto: 57.90,
      validade: '2022-12-31',
      id: 3,
    },
    {
      nome: 'Curso de .Net Core',
      precoProduto: 60.30,
      validade: '2022-12-31',
      id: 4,
    },
    {
      nome: 'Curso de ECMAScript',
      precoProduto: 70,
      validade: '2022-12-31',
      id: 5,
    },
  ];

  constructor() {}

  ngOnInit(): void {}
}
<h1 class="text-center text-danger mt-2">Seja bem vindo!</h1>

<!-- <h4 class="text-danger">{{ anuncio }}</h4> -->

<div class="row">
  <div class="col">
    <app-card-produto
      [foto]="foto"
      [nomeProduto]="nomeProduto"
      [promocao]="promocao"
      [idProduto]="idProduto"
      [dataValidade]="dataValidade"
      [precoProduto]="precoProduto"
    >
    </app-card-produto>
  </div>
  <div class="col">
    <app-card-produto
      foto="http://www.receitadeviagem.com.br/wp-content/uploads/2019/05/Viagem-para-Budapeste-87-Receita-de-Viagem-1024x680.jpg"
      nomeProduto="Viagem a budapest"
      [promocao]="false"
      [idProduto]="15"
      dataValidade="2021-12-31"
      [precoProduto]="5434.00"
    >
    </app-card-produto>
  </div>
</div>
